def compress(s):
    if not s:
        return s
    result = []
    current = s[0]
    count = 1
    for char in s[1:]:
        if char == current:
            count += 1
        else:
            result.append((current, count))
            current = char
            count = 1
    result.append((current, count))
    compressed = ''.join([f"{c}{n}" for c, n in result])
    if len(compressed) < len(s):
        return compressed
    else:
        return s
