def compress(input_string):
    if not input_string:
        return input_string

    compressed = []
    current_char = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current_char:
            count += 1
        else:
            compressed.append((current_char, count))
            current_char = char
            count = 1
    compressed.append((current_char, count))

    compressed_str = ''.join([f"{char}{count}" for char, count in compressed])

    if len(compressed_str) < len(input_string):
        return compressed_str
    else:
        return input_string
