from collections import Counter

def reorganizeString(s):
    freq = Counter(s)
    n = len(s)
    max_freq = max(freq.values()) if freq else 0
    if max_freq > (n + 1) // 2:
        return ""
    sorted_chars = sorted(freq.keys(), key=lambda x: -freq[x])
    result = []
    prev = None
    for _ in range(n):
        for c in sorted_chars:
            if c != prev:
                result.append(c)
                prev = c
                break
    return ''.join(result)

