def min_flips(s):
    count0 = 0
    count1 = 0
    for c in s:
        if c == '0':
            target0 = 0
            target1 = 1
        else:
            target0 = 1
            target1 = 0
        if c != target0:
            count0 += 1
        if c != target1:
            count1 += 1
    return min(count0, count1)
