def minFlips():
    S = input().strip()
    n = len(S)
    flip0 = 0  # Starting with '0'
    flip1 = 0  # Starting with '1'

    for i in range(n):
        if i % 2 == 0:
            expected0 = 0
            expected1 = 1
        else:
            expected0 = 1
            expected1 = 0

        if S[i] != expected0:
            flip0 += 1
        if S[i] != expected1:
            flip1 += 1

    return min(flip0, flip1)

# Example usage
S = input().strip()
print(minFlips())
