def minFlips(s):
    count0 = 0  # Count of flips needed if we follow the pattern starting with '0'
    count1 = 0  # Count of flips needed if we follow the pattern starting with '1'

    for i in range(len(s)):
        c = s[i]
        # Expected character for the pattern starting with '0'
        expected0 = 0 if i % 2 == 0 else 1
        # Expected character for the pattern starting with '1'
        expected1 = 1 if i % 2 == 0 else 0

        # Increment counts if the current character doesn't match the expected pattern
        if c != expected0:
            count0 += 1
        if c != expected1:
            count1 += 1

    # Return the minimum of the two counts
    return min(count0, count1)
