S = input().strip()
count0 = 0
count1 = 0
for i in range(len(S)):
    current = S[i]
    if i % 2 == 0:
        expected0 = '0'
        expected1 = '1'
    else:
        expected0 = '1'
        expected1 = '0'
    if current != expected0:
        count0 += 1
    if current != expected1:
        count1 += 1
print(min(count0, count1))
