def min_flips(s):
    count0 = 0  # Flips needed if the string starts with '0'
    count1 = 0  # Flips needed if the string starts with '1'

    for i in range(len(s)):
        # Calculate expected character for pattern starting with '0'
        if i % 2 == 0:
            expected0 = '0'
        else:
            expected0 = '1'
        if s[i] != expected0:
            count0 += 1

        # Calculate expected character for pattern starting with '1'
        if i % 2 == 0:
            expected1 = '1'
        else:
            expected1 = '0'
        if s[i] != expected1:
            count1 += 1

    return min(count0, count1)
