def solve_sudoku(board):
    def solve(board):
        # Find the first empty cell
        empty_cell = None
        for i in range(9):
            for j in range(9):
                if board[i][j] == '.':
                    empty_cell = (i, j)
                    break
            if empty_cell:
                break
        if not empty_cell:
            # No empty cells left, solution is found
            return True
        i, j = empty_cell

        # Generate possible numbers for the current cell
        possible = []
        for num in range(1, 10):
            if is_valid(board, i, j, num):
                possible.append(num)

        # Try each possible number
        for num in possible:
            board[i][j] = num
            if solve(board):
                return True
            board[i][j] = '.'  # backtrack

        return False

    def is_valid(board, row, col, num):
        # Check row
        for col in range(9):
            if board[row][col] == num:
                return False
        # Check column
        for row in range(9):
            if board[row][col] == num:
                return False
        # Check subgrid
        start_row = (row // 3) * 3
        start_col = (col // 3) * 3
        for i in range(start_row, start_row + 3):
            for j in range(start_col, start_col + 3):
                if board[i][j] == num:
                    return False
        return True

    solve(board)
    return board
