n = int(input())
operations = []
for _ in range(n):
    operations.append(input().strip())

inventory = {}

for op in operations:
    op_type, rest = op.split()
    parts = rest.split()
    id_part = parts[0]
    qty = int(parts[1])
    if op_type == '+':
        inventory[id_part] = inventory.get(id_part, 0) - qty
    else:
        inventory[id_part] = inventory.get(id_part, 0) + qty

q = int(input())
for _ in range(q):
    query = input().strip()
    if query in inventory:
        if inventory[query] > 0:
            print("in stock")
        else:
            print("out of stock")
    else:
        print("out of stock")
