def process_inventory():
    n = int(input())
    operations = []
    for _ in range(n):
        parts = input().split()
        op_type = parts[0]
        id = parts[1]
        qty = int(parts[2])
        operations.append((op_type, id, qty))

    inventory = {}
    for op in operations:
        op_type, id, qty = op
        if op_type == '+':
            if id in inventory:
                inventory[id] += qty
            else:
                inventory[id] = qty
        else:
            if id in inventory:
                inventory[id] -= qty
            else:
                inventory[id] = -qty  # Represents negative quantity

    q = int(input())
    for _ in range(q):
        id = input().strip()
        if id in inventory:
            if inventory[id] > 0:
                print("in stock")
            else:
                print("out of stock")
        else:
            print("out of stock")

if __name__ == "__main__":
    process_inventory()
