import math

def max_time_to_report_issue(n, r, t, coordinates):
    if r == 0:
        if n == 1:
            return 0
        else:
            return -1

    start = coordinates[0]
    max_time = 0
    for point in coordinates:
        x, y = point
        dx = x - start[0]
        dy = y - start[1]
        distance_sq = dx ** 2 + dy ** 2
        if distance_sq == 0:
            max_time = 0
            break
        distance = math.sqrt(distance_sq)
        time = math.ceil(distance / r)
        if time > t:
            return -1
        max_time = time
    return max_time if max_time <= t else -1
