def compress(input_string):
    if not input_string:
        return ""

    compressed = []
    current = input_string[0]
    count = 1

    for c in input_string[1:]:
        if c == current:
            count += 1
        else:
            compressed.append(current)
            current = c
            count = 1
    # Add the last current and count
    compressed.append(current)
    compressed.append(str(count))

    compressed_str = ''.join(compressed)
    original_length = len(input_string)
    compressed_length = len(compressed_str)

    if compressed_length < original_length:
        return compressed_str
    else:
        return input_string
