def update_inventory():
    # Read the number of inventory items
    n = int(input())
    inventory = {}
    for _ in range(n):
        parts = input().split()
        item = parts[0]
        quantity = int(parts[1])
        inventory[item] = quantity

    # Read the number of sales orders
    m = int(input())
    orders = []
    for _ in range(m):
        parts = input().split()
        item_order = parts[0]
        qty = int(parts[1])
        orders.append((item_order, qty))

    # Process each item in the inventory
    for item in inventory:
        current = inventory[item]
        for (o_item, o_qty) in orders:
            if o_item == item:
                current -= o_qty
                if current < 0:
                    current = 0
        inventory[item] = current

    # Output the results in the order they were input
    for item in inventory:
        print(f"{item} {inventory[item]}")

update_inventory()
