def update_inventory(n, inventory_list, m, orders_list):
    inventory = {}
    for _ in range(n):
        name, qty = inventory_list.readline().strip().split()
        inventory[name] = int(qty)

    orders = []
    for _ in range(m):
        name, qty = orders_list.readline().strip().split()
        orders.append((name, int(qty)))

    for name, qty in orders:
        current = inventory.get(name, 0)
        new_stock = max(0, current - qty)
        inventory[name] = new_stock

    for item in inventory:
        print(f"{item} {inventory[item]}")

