def update_inventory(n, inventory_list, m, orders_list):
    inventory = {}
    for item in inventory_list:
        name, qty = item.split()
        inventory[name] = int(qty)

    for item, qty in orders_list:
        if item in inventory:
            inventory[item] -= qty
            if inventory[item] < 0:
                inventory[item] = 0

    for item in inventory_list:
        print(f"{item} {inventory[item]}")

