# Read inventory details
inventory = {}
order_list = []
for _ in range(N):
    name, qty = input().split()
    inventory[name] = int(qty)
    order_list.append(name)

# Read sales orders
orders = []
for _ in range(M):
    name, qty = input().split()
    orders.append((name, qty))

# Process each order to update inventory
for order in orders:
    order_dict = {k: v for k, v in order}
    for item in order_dict:
        if order_dict[item] > inventory[item]:
            inventory[item] = 0
        else:
            inventory[item] -= order_dict[item]

# Output the remaining stock in the order they were added
for item in order_list:
    print(f"{item} {inventory[item]}")
