from collections import defaultdict

def reorganizeString(s):
    freq = defaultdict(int)
    for c in s:
        freq[c] += 1
    n = len(s)
    max_freq = max(freq.values())

    # Check if it's impossible to rearrange
    if max_freq > (n + 1) // 2:
        return ""

    # Build the result string
    result = []
    last_char = None
    for i in range(n):
        for char in sorted(freq.keys(), key=lambda x: -freq[x]):
            if char != last_char:
                if freq[char] > 0:
                    result.append(char)
                    freq[char] -= 1
                    last_char = char
                    break
    return ''.join(result)

