def is_valid(board, row, col, num):
    for i in range(9):
        if board[row][i] == num and i != col:
            return False
    for i in range(9):
        if board[i][col] == num and i != row:
            return False
    start_row = (row // 3) * 3
    start_col = (col // 3) * 3
    for i in range(3):
        for j in range(3):
            if board[start_row + i][start_col + j] == num and (start_row + i != row or start_col + j != col):
                return False
    return True

def solve_sudoku(board):
    for i in range(9):
        for j in range(9):
            if board[i][j] == '.':
                possible = []
                for num in range(1, 10):
                    if is_valid(board, i, j, num):
                        possible.append(num)
                possible.sort()
                for num in possible:
                    new_board = [row[:] for row in board]
                    new_board[i][j] = num
                    result = solve_sudoku(new_board)
                    if result is not None:
                        return result
                    new_board[i][j] = '.'  # backtrack
                return None  # No solution found
        if result is not None:
            return result
    return board
