import sys

def main():
    n = int(sys.stdin.readline())
    operations = []
    for _ in range(n):
        line = sys.stdin.readline().strip()
        parts = line.split()
        if not parts:
            continue  # handle any empty lines (though per problem statement, shouldn't happen)
        type_op, id, qty = parts
        type_op = type_op.strip()
        id = id.strip()
        qty = int(qy)
        operations.append((type_op, id, qty))

    from collections import defaultdict
    inventory = defaultdict(int)

    for op in operations:
        type_op, id, qty = op
        if type_op == '+':
            inventory[id] += qty
        else:
            inventory[id] -= qty

    q = int(sys.stdin.readline())
    for _ in range(q):
        item = sys.stdin.readline().strip()
        if item in inventory:
            if inventory[item] > 0:
                print("in stock")
            else:
                print("out of stock")
        else:
            print("out of stock")

if __name__ == "__main__":
    main()
