def process_inventory():
    n = int(input())
    inventory = {}

    for _ in range(n):
        line = input().strip()
        op_type, rest = line.split('+', 1)
        if op_type == '+':
            id = rest.split()[0]
            qty = int(rest.split()[1])
            inventory[id] = inventory.get(id, 0) + qty
        else:
            id = rest.split()[0]
            qty = int(rest.split()[1])
            inventory[id] = inventory.get(id, 0) - qty

    q = int(input())
    queries = [input().strip() for _ in range(q)]

    for item in queries:
        if item in inventory:
            qty = inventory[item]
            print("in stock" if qty > 0 else "out of stock")
        else:
            print("out of stock")

process_inventory()
