from collections import deque

def is_path_possible(n, m, blocked_cells):
    if n == 0 or m == 0:
        return False  # Though according to the problem constraints, n and m are >=1

    # Create a blocked grid
    blocked = [[False for _ in range(m)] for _ in range(n)]
    for (i, j) in blocked_cells:
        if 0 <= i < n and 0 <= j < m:
            blocked[i][j] = True

    # Check if starting cell is blocked
    if (0, 0) in blocked:
        if n == 1 and m == 1:
            return True
        else:
            return False

    # BFS initialization
    visited = [[False for _ in range(m)] for _ in range(n)]
    queue = deque()
    queue.append((0, 0))
    visited[0][0] = True

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    end = (n-1, m-1)

    while queue:
        i, j = queue.popleft()

        if (i, j) == end:
            return True

        for dx, dy in directions:
            ni, nj = i + dx, j + dy
            if 0 <= ni < n and 0 <= nj < m:
                if not visited[ni][nj] and not blocked[ni][nj]:
                    visited[ni][nj] = True
                    queue.append((ni, nj))

    return False

