from collections import deque

def is_path_possible(n, m, blocked_cells):
    if n == 0 or m == 0:
        return False  # According to problem constraints, n and m are at least 1, so this is redundant

    # Check if starting cell is blocked
    if (1, 1) in blocked_cells:
        return False

    # Check if target cell is blocked
    if (n, m) == (1, 1):
        return True

    # Convert blocked cells to a set for O(1) lookups
    blocked = set((i, j) for (i, j) in blocked_cells)

    # Initialize visited matrix
    visited = [[False for _ in range(m)] for _ in range(n)]
    queue = deque()
    queue.append((0, 0))
    visited[0][0] = True

    # Define the four possible directions
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]  # Right, Down, Left, Up

    while queue:
        i, j = queue.popleft()
        for dx, dy in directions:
            ni, nj = i + dx, j + dy
            if 0 <= ni < n and 0 <= nj < m:
                if (ni, nj) not in blocked and not visited[ni][nj]:
                    visited[ni][nj] = True
                    if ni == n - 1 and nj == m - 1:
                        return True
                    queue.append((ni, nj))

    return False

