def compress(input_string):
    if not input_string:
        return input_string

    compressed = []
    current = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current:
            count += 1
        else:
            compressed.append((current, count))
            current = char
            count = 1
    compressed.append((current, count))

    comp_str = ''.join([f"{c}{n}" for c, n in compressed])

    if len(comp_str) < len(input_string):
        return comp_str
    else:
        return input_string
