def compress(s):
    if not s:
        return s

    compressed = []
    current_char = s[0]
    count = 1

    for char in s[1:]:
        if char == current_char:
            count += 1
        else:
            compressed.append((current_char, count))
            current_char = char
            count = 1
    # Add the last run
    compressed.append((current_char, count))

    # Construct the result string
    result = []
    for char, cnt in compressed:
        result.append(f"{char}{cnt}")

    return ''.join(result)
