def compress(input_string):
    if not input_string:
        return input_string

    runs = []
    prev_char = input_string[0]
    count = 1

    for c in input_string[1:]:
        if c == prev_char:
            count += 1
        else:
            runs.append((prev_char, count))
            prev_char = c
            count = 1
    runs.append((prev_char, count))

    compressed = []
    for char, cnt in runs:
        if cnt == 1:
            compressed.append(char)
        else:
            compressed.append((char, cnt))

    compressed_str = ''.join(compressed)

    return compressed_str if len(compressed_str) < len(input_string) else input_string
