def compress(s):
    if not s:
        return s
    res = []
    current = s[0]
    count = 1
    for i in range(1, len(s)):
        if s[i] == current:
            count += 1
        else:
            res.append(current + str(count))
            current = s[i]
            count = 1
    res.append(current + str(count))
    compressed = ''.join(res)
    if len(compressed) < len(s):
        return compressed
    else:
        return s
