def compress(input_string):
    if not input_string:
        return input_string
    compressed = ""
    current_char = input_string[0]
    current_count = 1
    for char in input_string[1:]:
        if char == current_char:
            current_count += 1
        else:
            compressed += current_char + str(current_count)
            current_char = char
            current_count = 1
    # Add the last group
    compressed += current_char + str(current_count)
    # Compare lengths
    if len(compressed) < len(input_string):
        return compressed
    else:
        return input_string
