def compress(input_string):
    if len(input_string) <= 1:
        return input_string

    result = []
    current_char = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current_char:
            count += 1
        else:
            result.append((current_char, count))
            current_char = char
            count = 1

    result.append((current_char, count))

    compressed = ''.join(f"{c}{n}" for (c, n) in result)

    if len(compressed) < len(input_string):
        return compressed
    else:
        return input_string
