def compress(input_string):
    if not input_string:
        return input_string

    prev_char = input_string[0]
    count = 1
    result = [(prev_char, count)]

    for char in input_string[1:]:
        if char == prev_char:
            count += 1
        else:
            result.append((prev_char, count))
            prev_char = char
            count = 1
    result.append((prev_char, count))

    compressed = ''.join([f"{c}{n}" for (c, n) in result])

    if len(compressed) < len(input_string):
        return compressed
    else:
        return input_string
