def update_inventory(n, inventory_list, m, orders_list):
    # Read the inventory items
    inventory = {}
    for item in inventory_list:
        name = item[0]
        qty = item[1]
        inventory[name] = qty

    # Process each sales order
    for order in orders_list:
        item_name = order[0]
        qty_ordered = order[1]
        current_stock = inventory.get(item_name, 0)
        new_stock = current_stock - qty_ordered
        if new_stock < 0:
            new_stock = 0
        inventory[item_name] = new_stock

    # Output the inventory in the order they were received
    for item in inventory:
        print(f"{item} {inventory[item]}")
