from collections import Counter

def reorganizeString(s):
    if not s:
        return ""

    counts = Counter(s)

    # Sort characters by frequency descending, then by character ascending
    sorted_chars = sorted(counts.items(), key=lambda x: (-x[1], x[0]))

    last_char = None
    result = []

    for char, cnt in sorted_chars:
        if cnt == 0:
            continue

        if last_char is not None and char == last_char:
            continue  # same as last, can't add

        if char != last_char:
            result.append(char)
            last_char = char
            counts[char] -= 1

    if len(result) != len(s):
        return ""

    return ''.join(result)

