def solve_sudoku(board):
    def find_empty():
        for i in range(9):
            for j in range(9):
                if board[i][j] == '.':
                    return (i, j)
        return None

    def is_valid(r, c, num):
        # Check row
        for i in range(9):
            if board[r][i] == num and i != c:
                return False
        # Check column
        for i in range(9):
            if board[i][c] == num and i != r:
                return False
        # Check subgrid
        start_r = (r // 3) * 3
        start_c = (c // 3) * 3
        for i in range(start_r, start_r + 3):
            for j in range(start_c, start_c + 3):
                if board[i][j] == num and (i, j) != (r, c):
                    return False
        return True

    def solve():
        empty_pos = find_empty()
        if not empty_pos:
            return True
        row, col = empty_pos
        for num in range(1, 10):
            if is_valid(row, col, num):
                board[row][col] = num
                if solve():
                    return True
                board[row][col] = 0  # Backtrack
        return False

    return solve

def solve(board):
    # Make a copy to avoid modifying the original
    copied_board = [row[:] for row in board]
    solve_sudoku(copied_board)
    return copied_board
