def process_inventory(n, operations, q, queries):
    inventory = {}
    for op in operations:
        parts = op.split()
        op_type = parts[0]
        rest = parts[1:]
        if len(rest) < 2:
            continue
        id_part = rest[0].strip()
        qty = int(rest[1].strip())
        if op_type == '+':
            inventory[id_part] = qty
        else:
            inventory[id_part] = -qty
    for item in queries:
        if item in inventory:
            if inventory[item] > 0:
                print("in stock")
            else:
                print("out of stock")
        else:
            print("out of stock")

# Read input
n = int(input())
operations = [input().strip() for _ in range(n)]
q = int(input())
queries = [input().strip() for _ in range(q)]

# Process inventory and queries
process_inventory(n, operations, q, queries)
