n = int(input())
inventory = {}

for _ in range(n):
    line = input().strip()
    parts = line.split()
    if parts[1] == '+':
        id = parts[2]
        qty = int(parts[3])
        inventory[id] = inventory.get(id, 0) + qty
    else:
        id = parts[2]
        qty = int(parts[3])
        inventory[id] = inventory.get(id, 0) - qty

q = int(input())
for _ in range(q):
    query = input().strip()
    if query in inventory:
        stock = inventory[query]
        if stock > 0:
            print("in stock")
        else:
            print("out of stock")
    else:
        print("out of stock")
