def process_inventory():
    n = int(input())
    current_dict = {}
    for _ in range(n):
        op = input().strip()
        tokens = op.split()
        if not tokens:
            continue  # Skip empty lines (though per problem statement they shouldn't be present)
        operation_type = tokens[0]
        id_part = tokens[1]
        qty = int(tokens[2])
        if operation_type == '+':
            current_dict[id_part] = current_dict.get(id_part, 0) + qty
        else:
            current_dict[id_part] = current_dict.get(id_part, 0) - qty
    q = int(input())
    for _ in range(q):
        item = input().strip()
        if item in current_dict:
            if current_dict[item] > 0:
                print("in stock")
            else:
                print("out of stock")
        else:
            print("out of stock")

if __name__ == "__main__":
    process_inventory()
