def is_path_possible(n, m, blocked_cells):
    if n == 1 and m == 1:
        return True
    start = (0, 0)
    end = (n - 1, m - 1)

    if start in blocked_cells or end in blocked_cells:
        return False

    blocked = set(blocked_cells)
    from collections import deque
    queue = deque([start])
    visited = set([start])

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    while queue:
        i, j = queue.popleft()
        if (i, j) == end:
            return True
        for di, dj in directions:
            ni = i + di
            nj = j + dj
            if 0 <= ni < n and 0 <= nj < m:
                if (ni, nj) not in visited and (ni, nj) not in blocked:
                    visited.add((ni, nj))
                    queue.append((ni, nj))

    return False

