def min_operations_to_transform(Y):
    if Y == 0:
        return 0  # though Y >= 1 per problem statement
    count = bin(Y).count('1')
    highest_bit_pos = Y.bit_length() - 1 if Y != 0 else 0
    return count + (highest_bit_pos - 1)

T = int(input())
for _ in range(T):
    Y = int(input())
    print(min_operations_to_transform(Y))
