def update_inventory(n, inventory_list, m, orders_list):
    # Initialize the inventory dictionary
    inventory = {}
    for item, qty in inventory_list:
        inventory[item] = qty

    # Process each sales order
    for order in orders_list:
        item, qty = order
        if item in inventory:
            if qty > inventory[item]:
                inventory[item] = 0
            else:
                inventory[item] -= qty
                if inventory[item] < 0:
                    inventory[item] = 0

    # Collect the items in the order they were input
    output = []
    for item, qty in inventory_list:
        output.append((item, qty))

    # Print each item's name and remaining quantity
    for item, qty in output:
        print(f"{item} {qty}")
