def letter_frequency_analyzer(input_lines):
    lines = [line.strip() for line in input_lines if line.strip() != '.']
    most_common = []
    for s in lines:
        freq = {chr(ord('a') + i): 0 for i in range(26)}
        for c in s:
            if c.islower():
                freq[c] += 1
        max_freq = max(freq.values()) if freq else 0
        most_common = [k for k, v in freq.items() if v == max_freq]
        most_common_sorted = sorted(most_common)
        print(' '.join(most_common_sorted))

if __name__ == "__main__":
    import sys
    input_lines = sys.stdin.read().splitlines()
    letter_frequency_analyzer(input_lines)

