n, m = map(int, input().split())
grid = [list(map(int, input().split())) for _ in range(n)]

# Initialize DP table
dp = [[1 for _ in range(m)] for _ in range(n)]
max_size = 1

for i in range(n):
    for j in range(m):
        if i == 0 or j == 0:
            dp[i][j] = 1
        else:
            if grid[i][j] == dp[i-1][j] and grid[i][j] == dp[i][j-1] and grid[i][j] == dp[i-1][j-1]:
                dp[i][j] = dp[i-1][j] + dp[i][j-1] - dp[i-1][j-1] + 1
            else:
                dp[i][j] = 1
        if dp[i][j] > max_size:
            max_size = dp[i][j]

print(max_size)
