def compress(s):
    if not s:
        return s
    compressed = []
    current_char = s[0]
    count = 1
    for char in s[1:]:
        if char == current_char:
            count += 1
        else:
            compressed.append((current_char, count))
            current_char = char
            count = 1
    compressed.append((current_char, count))
    compressed_str = ''.join([t[0] + str(t[1]) for t in compressed])
    if len(compressed_str) < len(s):
        return compressed_str
    else:
        return s
