def compress(s):
    if not s:
        return s
    compressed = []
    current = s[0]
    count = 1
    for c in s[1:]:
        if c == current:
            count += 1
        else:
            compressed.append((current, count))
            current = c
            count = 1
    compressed.append((current, count))
    compressed_str = []
    for char, cnt in compressed:
        compressed_str.append(f"{char}{cnt}")
    compressed_str = ''.join(compressed_str)
    if len(compressed_str) < len(s):
        return compressed_str
    else:
        return s
