def compress(input_string):
    if not input_string:
        return input_string

    groups = []
    current_char = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current_char:
            count += 1
        else:
            groups.append((current_char, count))
            current_char = char
            count = 1

    groups.append((current_char, count))
    compressed = ''.join([f"{char}{count}" if count != 1 else char for (char, count) in groups])

    if len(compressed) >= len(input_string):
        return input_string
    else:
        return compressed
