def solve_sudoku(board):
    def find_empty(board):
        for row in range(9):
            for col in range(9):
                if board[row][col] == '.':
                    return (row, col)
        return None

    def is_valid(board, row, col, num):
        # Check row
        for c in range(9):
            if board[row][c] == num:
                return False
        # Check column
        for r in range(9):
            if board[r][col] == num:
                return False
        # Check subgrid
        start_row = 3 * (row // 3)
        start_col = 3 * (col // 3)
        for r in range(start_row, start_row+3):
            for c in range(start_col, start_col+3):
                if board[r][c] == num:
                    return False
        return True

    def solve(current_board):
        empty_pos = find_empty(current_board)
        if not empty_pos:
            return current_board
        row, col = empty_pos
        for num in range(1, 10):
            if is_valid(current_board, row, col, num):
                current_board[row][col] = num
                result = solve(current_board)
                if result is not None:
                    return result
                current_board[row][col] = 0  # backtrack
        return None

    current = [row.copy() for row in board]
    result = solve(current)
    return result
