n, w = map(int, input().split())
flowers = []
for _ in range(n):
    a, b = map(int, input().split())
    flowers.append((a, b))

if not flowers:
    print(0)
else:
    flowers.sort()
    sum_water = 0
    count = 0
    for ai, bi in flowers:
        if sum_water + ai <= w:
            sum_water += ai
            count += 1
        else:
            break
    print(count)
