def main():
    import sys
    input = sys.stdin.read().splitlines()
    ptr = 0
    n = int(input[ptr])
    ptr += 1

    operations = {}
    for _ in range(n):
        op = input[ptr].strip()
        ptr += 1
        if op.startswith('+'):
            id = op.split()[1]
            qty = int(op.split()[2])
            operations[id] = operations.get(id, 0) + qty
        else:
            id = op.split()[1]
            qty = int(op.split()[2])
            operations[id] = operations.get(id, 0) - qty

    q = int(input[ptr])
    ptr += 1
    for _ in range(q):
        item = input[ptr].strip()
        ptr += 1
        if item in operations:
            if operations[item] > 0:
                print("in stock")
            else:
                print("out of stock")
        else:
            print("out of stock")

if __name__ == "__main__":
    main()
