import re

def process_inventory():
    n = int(input())
    inventory = {}
    for _ in range(n):
        line = input().strip()
        match = re.match(r'^([+-])?([a-zA-Z]+)(\d+)(\s*)$', line)
        if match:
            op_type, item, qty_str, _ = match.groups()
            qty = int(qty_str.strip())
            if op_type == '+':
                inventory[item] = qty
            else:
                inventory[item] -= qty
    q = int(input())
    for _ in range(q):
        item = input().strip()
        if item in inventory:
            qty = inventory[item]
            if qty > 0:
                print("in stock")
            else:
                print("out of stock")
        else:
            print("out of stock")

if __name__ == "__main__":
    process_inventory()
