n = int(input())
operations = [input().strip() for _ in range(n)]
q = int(input())
queries = [input().strip() for _ in range(q)]

stock = {}

for op in operations:
    parts = op.split()
    op_type = parts[0]
    if op_type == '+':
        quantity = int(parts[2:])
        stock[parts[1]] = stock.get(parts[1], 0) + quantity
    else:
        quantity = int(parts[2:])
        stock[parts[1]] = stock.get(parts[1], 0) - quantity

for item in queries:
    if item in stock and stock[item] > 0:
        print("in stock")
    else:
        print("out of stock")
