def main():
    import sys
    input = sys.stdin.read().split()
    ptr = 0

    n = int(input[ptr])
    ptr += 1
    operations = []
    for _ in range(n):
        op = input[ptr:ptr+3]  # Assuming each operation is 3 tokens
        ptr += 3
        operations.append(op)

    inventory = {}

    for op in operations:
        type_part = op[:2]
        rest = op[2:]
        id_part = rest[0]
        qty = int(rest[1:])
        if type_part == '+':
            if id_part in inventory:
                inventory[id_part] += qty
            else:
                inventory[id_part] = qty
        else:
            if id_part in inventory:
                inventory[id_part] -= qty
            else:
                inventory[id_part] = -qty

    q = int(input[ptr])
    ptr += 1
    for _ in range(q):
        item = input[ptr]
        ptr += 1
        if item in inventory:
            if inventory[item] > 0:
                print("in stock")
            else:
                print("out of stock")
        else:
            print("out of stock")

if __name__ == "__main__":
    main()
